declare abstract class API<Events extends Object> {
    id: string;
    listeners: { [event in keyof Events]: ((data: Events[event]) => void)[]; };
    protected socketEvent(raw: string): void;
    /**
     * Listens to events from the client
     * @param event Event name
     * @param data Event data
     */
    on<T extends keyof Events>(event: T, callback: (data: Events[T]) => void): void;
}
export default API;
