import API from './api';
import { IClient, IServer } from './types';
declare class Client extends API<IServer.Events> {
    iframe: HTMLIFrameElement;
    constructor(options: IClient.Options);
    /**
     * Emits an event to the client
     * @param event Event name
     * @param data Event data
     */
    emit<T extends IClient.Event>(event: T, data: IClient.Events[T]): boolean;
    server: {
        emit: <T extends "message" | "signIn" | "messageUpdate" | "messageDelete" | "messageReactionAdd" | "messageReactionRemove">(event: T, data: IServer.Events[T]) => void;
    };
}
export default Client;
