import API from './api';
import { IClient, IServer } from './types';
declare class Server extends API<IClient.Events> {
    targetOrigin: string;
    constructor(options: IServer.Options);
    /**
     * Emits an event to the client
     * @param event Event name
     * @param data Event data
     */
    emit<T extends IServer.Event>(event: T, data: IServer.Events[T]): boolean;
    client: {
        emit: <T extends "sendMessage">(event: T, data: IClient.Events[T]) => void;
    };
}
export default Server;
